            <input 
              type="number"
              placeholder="Digite sua altura (Ex: 1.58)"
              value={heightField > 0 ? heightField : ''}
              /* onChange={} */
            />
          </div>

          <div className={styles.rightSide}>
            ---
          </div>
        </div>
    </div>
  )
}

export default App;
